-- Consulta para ver ejercicios con cÃ³digo base y resultado esperado
SELECT 
    t.id,
    c.nombre as categoria,
    n.nombre as nivel,
    LEFT(t.descripcion, 100) as descripcion_preview,
    CASE 
        WHEN t.codigo_base IS NOT NULL THEN 'SÃ­'
        ELSE 'No'
    END as tiene_codigo_base,
    CASE 
        WHEN t.resultado_esperado IS NOT NULL THEN 'SÃ­'
        ELSE 'No'
    END as tiene_resultado_esperado
FROM tareas t
JOIN categorias c ON t.categoria_id = c.id
JOIN niveles n ON t.nivel_id = n.id
ORDER BY c.nombre, n.nombre;

